import './App.css';
import {useState, useEffect} from 'react';
import bluePiece from "./images/blue.png";
import orangePiece from "./images/orange.png";
import redPiece from "./images/red.png";
import greenPiece from "./images/green.png";

const ogPieces = [
  {
    key: 0, type: 1, orientation: 1, location: "shuffled"
  },
  {
    key: 1, type: 1, orientation: 1, location: "shuffled"
  },
  {
    key: 2, type: 1, orientation: 1, location: "shuffled"
  },
  {
    key: 3, type: 1, orientation: 1, location: "shuffled"
  },
  {
    key: 4, type: 2, orientation: 1, location: "shuffled"
  },
  {
    key: 5, type: 2, orientation: 1, location: "shuffled"
  },
  {
    key: 6, type: 3, orientation: 1, location: "shuffled"
  },
  {
    key: 7, type: 3, orientation: 1, location: "shuffled"
  },
  {
    key: 8, type: 4, orientation: 1, location: "shuffled"
  }
]

// function app() {
//   const [shuffled, setShuffled] = useState(pieces);

// }

function App() {
  const [pieces, setPieces] = useState([1, 2, 3, 4, 5, 6, 7, 8, 9]);
  const [topLeft, setTopLeft] = useState(1);
  const [top, setTop] = useState(2);
  const [topRight, setTopRight] = useState(3);
  const [middleLeft, setMiddleLeft] = useState(4);
  const [middle, setMiddle] = useState(5);
  const [middleRight, setMiddleRight] = useState(6);
  const [bottomLeft, setBottomLeft] = useState(7);
  const [bottom, setBottom] = useState(8);
  const [bottomRight, setBottomRight] = useState(9);
  const settings = [setTopLeft, setTop, 
  setTopRight, setMiddleLeft, setMiddle, 
  setMiddleRight, setBottomLeft, setBottom, 
  setBottomRight];

  const [shuffled, setShuffled] = useState(ogPieces);
  console.log("hello", shuffled);

  const [clickedPiece, setClickedPiece] = useState(0);

  const clickPiece = (num) => {
    console.log("clickPiece");
    if (num === clickedPiece) {
      setClickedPiece(0);
    } else {
      setClickedPiece(num);
    }

    setPieces(pieces);
  }

  const clickBoardPiece = (event) => {
    console.log("clickboardpiece");
    console.log("pieces: ", pieces);
    setPieces((oldpieces) => {
      console.log("oldpieces", oldpieces);
      console.log("hello", event.currentTarget.textContent);
      oldpieces.push(parseInt(event.currentTarget.textContent));
      return oldpieces;
    });
    document.getElementById(event.currentTarget.getAttribute("spot")).setAttribute("has-piece", "0");
    settings[event.currentTarget.getAttribute("spot") - 1](event.currentTarget.getAttribute("spot"));
  }

  const clickBoard = (event) => {
    console.log("clickBoard");
    console.log("spot is ", event.currentTarget.getAttribute("id"));
    console.log(event.currentTarget);
    let spot = event.currentTarget.getAttribute("id");
    if (clickedPiece != 0 && event.currentTarget.getAttribute("has-piece") == '0') {
      let newPieces = pieces.filter(piece => piece != clickedPiece);
      setPieces((oldPieces) => {return newPieces});
      settings[parseInt(spot) - 1](<article key={clickedPiece} spot={spot} onClick={(e) => {clickBoardPiece(e)}}>{clickedPiece}</article>)
      setClickedPiece(0);
      event.currentTarget.setAttribute('has-piece', '1');
    }
  }

  useEffect(() => {
    console.log("called useeffect");
    setPieces(pieces);
    setClickedPiece(clickedPiece);
    console.log("pieces ", pieces);
    console.log("clicked Piece ", clickedPiece);
  });

  return (
    <>
      <div>
        <table>
          <tbody>
            <tr>
              <td key="1" id="1" has-piece='0' onClick={(e) => {clickBoard(e)}}>{topLeft}</td>
              <td key="2" id="2" has-piece='0' onClick={(e) => {clickBoard(e)}}>{top}</td>
              <td key="3" id="3" has-piece='0' onClick={(e) => {clickBoard(e)}}>{topRight}</td>
            </tr>
            <tr>
              <td key="4" id="4" has-piece='0' onClick={(e) => {clickBoard(e)}}>{middleLeft}</td>
              <td key="5" id="5" has-piece='0' onClick={(e) => {clickBoard(e)}}>{middle}</td>
              <td key="6" id="6" has-piece='0' onClick={(e) => {clickBoard(e)}}>{middleRight}</td>
            </tr>
            <tr>
              <td key="7" id="7" has-piece='0' onClick={(e) => {clickBoard(e)}}>{bottomLeft}</td>
              <td key="8" id="8" has-piece='0' onClick={(e) => {clickBoard(e)}}>{bottom}</td>
              <td key="9" id="9" has-piece='0' onClick={(e) => {clickBoard(e)}}>{bottomRight}</td>
            </tr>
          </tbody>

      </table>
      <button onClick={() => {console.log(pieces)}}>Pieces</button>
      <button onClick={() => {console.log(clickedPiece)}}>ClickedPiece</button>
      <div className='pieces'>
        {shuffled.map((piece) => {
            return (
              <Piece 
                pieceDict={piece} 
                clickPiece={clickPiece} 
                clickBoard={clickBoard}
                clicked={(piece.key == clickedPiece)}
              />
            )
          }
        )}
      </div>
      </div>
    </>
  );
}

const Piece = (props) => {
  const {pieceDict, clickPiece, clickBoardPiece, clicked} = props;
  if (clicked) {
    return (
      <article className="Clicked" key={pieceDict.key} onClick={() => {clickPiece()}}>
        {pieceDict.type}
      </article>
      )
  } else {
    return (
      <article key={pieceDict.key} onClick={() => {clickPiece(pieceDict.key)}}>
        {pieceDict.type}
      </article>
    )
  }

}

export default App;
