import logo from './logo.svg';
import './App.css';
import {useState} from 'react';



function App() {
  const [pieces, setPieces] = useState([1, 2, 3, 4, 5, 6, 7, 8, 9]);
  const [topLeft, setTopLeft] = useState(1);
  const [top, setTop] = useState(2);
  const [topRight, setTopRight] = useState(3);
  const [middleLeft, setMiddleLeft] = useState(4);
  const [middle, setMiddle] = useState(5);
  const [middleRight, setMiddleRight] = useState(6);
  const [bottomLeft, setBottomLeft] = useState(7);
  const [bottom, setBottom] = useState(8);
  const [bottomRight, setBottomRight] = useState(9);
  const settings = [setTopLeft, setTop, 
  setTopRight, setMiddleLeft, setMiddle, 
  setMiddleRight, setBottomLeft, setBottom, 
  setBottomRight];

  let clickedPiece = 0;
  const clickPiece = (num) => {
    if (num === clickedPiece) {
      clickedPiece = 0;
    } else {
      clickedPiece = num;
    }
    setPieces(pieces);
    console.log("Clicked piece is ", clickedPiece);
  }

  const clickBoard = (event) => {
    console.log("spot is ", event.currentTarget.getAttribute("data-key"));
    let spot = event.currentTarget.getAttribute("data-key");
    if (clickPiece !== 0) {
      let newPieces = pieces.filter(piece => piece != clickedPiece);
      setPieces(newPieces);
      console.log(pieces);
      settings[parseInt(spot) - 1](<article key={clickedPiece} onClick={() => {clickPiece(clickedPiece)}}>{clickedPiece}</article>)
    }

  }
  return (
    <>
      <div>
        <table>
          <tbody>
            <tr>
              <td key="1" data-key="1" onClick={(e) => {clickBoard(e)}}>{topLeft}</td>
              <td key="2" data-key="2" onClick={(e) => {clickBoard(e)}}>{top}</td>
              <td key="3" data-key="3" onClick={(e) => {clickBoard(e)}}>{topRight}</td>
            </tr>
            <tr>
              <td key="4" data-key="4" onClick={(e) => {clickBoard(e)}}>{middleLeft}</td>
              <td key="5" data-key="5" onClick={(e) => {clickBoard(e)}}>{middle}</td>
              <td key="6" data-key="6" onClick={(e) => {clickBoard(e)}}>{middleRight}</td>
            </tr>
            <tr>
              <td key="7" data-key="7" onClick={(e) => {clickBoard(e)}}>{bottomLeft}</td>
              <td key="8" data-key="8" onClick={(e) => {clickBoard(e)}}>{bottom}</td>
              <td key="9" data-key="9" onClick={(e) => {clickBoard(e)}}>{bottomRight}</td>
            </tr>
          </tbody>

      </table>
      <div>
        {pieces.map((piece) => {
          if (piece == clickedPiece) {
            console.log("poopy");
            return <article style="border-color: green;" key={piece} onClick={() => {clickPiece(piece)}}>{piece}</article>
          } else {
            console.log("snoopy");
            return <article key={piece} onClick={() => {clickPiece(piece)}}>{piece}</article>
          }
        })}
      </div>
      </div>
    </>
  );
}

export default App;
