import logo from './logo.svg';
import './App.css';
import {useState} from 'react';



function App() {
  const [pieces, setPieces] = useState([1, 2, 3, 4, 5, 6, 7, 8, 9]);
  const [topLeft, setTopLeft] = useState(1);
  const [top, setTop] = useState(2);
  const [topRight, setTopRight] = useState(3);
  const [middleLeft, setMiddleLeft] = useState(4);
  const [middle, setMiddle] = useState(5);
  const [middleRight, setMiddleRight] = useState(6);
  const [bottomLeft, setBottomLeft] = useState(7);
  const [bottom, setBottom] = useState(8);
  const [bottomRight, setBottomRight] = useState(9);

  let clickedPiece = 0;
  const clickPiece = (num) => {
    if (num === clickedPiece) {
      clickedPiece = 0;
    } else {
      clickedPiece = num;
    }

    console.log(clickedPiece);
  }

  const clickBoard = (event) => {
    console.log(event.currentTarget.getAttribute("data-key"));
    if (clickPiece != 0) {
      let newPieces = [];
      switch(event.currentTarget.getAttribute("data-key")) {
        case "1":
          newPieces = pieces.filter(piece => piece != 1);
          setPieces(newPieces);
          console.log(pieces);
          setTopLeft(<article key={1} onClick={() => {clickPiece(1)}}>{1}</article>)
          break;
        case "2":
          newPieces = pieces.filter(piece => piece != 2);
          setPieces(newPieces);
          console.log(pieces);
          setTop(<article key={2} onClick={() => {clickPiece(2)}}>{2}</article>)
          break;
        case "3":
          newPieces = pieces.filter(piece => piece != 1);
          setPieces(newPieces);
          console.log(pieces);
          setTopLeft(<article key={1} onClick={() => {clickPiece(1)}}>{1}</article>)
          break;
        case "4":
          newPieces = pieces.filter(piece => piece != 1);
          setPieces(newPieces);
          console.log(pieces);
          setTopLeft(<article key={1} onClick={() => {clickPiece(1)}}>{1}</article>)
          break;
        case "5":
          newPieces = pieces.filter(piece => piece != 1);
          setPieces(newPieces);
          console.log(pieces);
          setTopLeft(<article key={1} onClick={() => {clickPiece(1)}}>{1}</article>)
          break;
        case "6":
          newPieces = pieces.filter(piece => piece != 1);
          setPieces(newPieces);
          console.log(pieces);
          setTopLeft(<article key={1} onClick={() => {clickPiece(1)}}>{1}</article>)
          break;
        case "7":
          newPieces = pieces.filter(piece => piece != 1);
          setPieces(newPieces);
          console.log(pieces);
          setTopLeft(<article key={1} onClick={() => {clickPiece(1)}}>{1}</article>)
          break;
        case "8":
          newPieces = pieces.filter(piece => piece != 1);
          setPieces(newPieces);
          console.log(pieces);
          setTopLeft(<article key={1} onClick={() => {clickPiece(1)}}>{1}</article>)
          break;
        case "9":
          newPieces = pieces.filter(piece => piece != 1);
          setPieces(newPieces);
          console.log(pieces);
          setTopLeft(<article key={1} onClick={() => {clickPiece(1)}}>{1}</article>)
          break;
      }
    }

  }
  return (
    <>
      <div>
        <table>
          <tbody>
            <tr>
              <td key="1" data-key="1" onClick={(e) => {clickBoard(e)}}>{topLeft}</td>
              <td key="2" data-key="2" onClick={(e) => {clickBoard(e)}}>{top}</td>
              <td key="3" data-key="3" onClick={(e) => {clickBoard(e)}}>{topRight}</td>
            </tr>
            <tr>
              <td key="4" data-key="4" onClick={(e) => {clickBoard(e)}}>{middleLeft}</td>
              <td key="5" data-key="5" onClick={(e) => {clickBoard(e)}}>{middle}</td>
              <td key="6" data-key="6" onClick={(e) => {clickBoard(e)}}>{middleRight}</td>
            </tr>
            <tr>
              <td key="7" data-key="7" onClick={(e) => {clickBoard(e)}}>{bottomLeft}</td>
              <td key="8" data-key="8" onClick={(e) => {clickBoard(e)}}>{bottom}</td>
              <td key="9" data-key="9" onClick={(e) => {clickBoard(e)}}>{bottomRight}</td>
            </tr>
          </tbody>

      </table>
      <div>
        {pieces.map((piece) => {
          return <article key={piece} onClick={() => {clickPiece(piece)}}>{piece}</article>
        })}
      </div>
      </div>
    </>
  );
}

export default App;
