import './App.css';
import {useState, useEffect} from 'react';
import bluePiece from "./images/blue.png";
import orangePiece from "./images/orange.png";
import redPiece from "./images/red.png";
import greenPiece from "./images/green.png";

const ogPieces = [
  {
    key: 0, type: orangePiece, orientation: 1, location: "shuffled"
  },
  {
    key: 1, type: orangePiece, orientation: 1, location: "shuffled"
  },
  {
    key: 2, type: orangePiece, orientation: 1, location: "shuffled"
  },
  {
    key: 3, type: orangePiece, orientation: 1, location: "shuffled"
  },
  {
    key: 4, type: bluePiece, orientation: 1, location: "shuffled"
  },
  {
    key: 5, type: bluePiece, orientation: 1, location: "shuffled"
  },
  {
    key: 6, type: greenPiece, orientation: 1, location: "shuffled"
  },
  {
    key: 7, type: greenPiece, orientation: 1, location: "shuffled"
  },
  {
    key: 8, type: redPiece, orientation: 1, location: "shuffled"
  }
]

function App() {
  const [shuffled, setShuffled] = useState(ogPieces);
  const [board, setBoard] = useState([0,0,0,0,0,0,0,0,0]);
  console.log("Shuffled: ", shuffled);

  const [clickedPiece, setClickedPiece] = useState({key: -1});

  const clickPiece = (piece, event) => {
    console.log("sender is :", event.target);
    if (piece.location === "shuffled") {
      console.log("clickPiece");
      if (clickedPiece.key === piece.key) {
        setClickedPiece({key: -1});
      } else {
        setClickedPiece(piece);
      }
    }
  }

  const doubleClickPiece = (event) => {
    console.log("doubleclick: ", event.currentTarget);
    console.log(event.currentTarget.id);
    let key = event.currentTarget.id;
    let pieceIndex = shuffled.findIndex(piece => piece.key === key);
    console.log(shuffled[0]);
    // let rotation = shuffled[pieceIndex].orientation++ + 1;
    // event.currentTarget.querySelector("img").style.transform = "rotate(" + (90 * (rotation - 1)) + ")"; 
  }

  const clickBoard = (event) => {
    console.log("clickBoard");
    console.log("This spot is ", event.currentTarget.getAttribute("id"));
    let id = event.currentTarget.getAttribute("id")
    let hasPiece = event.currentTarget.getAttribute("has-piece");
    if (!(clickedPiece.key === -1)) {
      if (hasPiece === "No") {
        event.currentTarget.setAttribute("has-piece", "Yes");

        // Updating shuffled
        console.log("key is ", shuffled.findIndex(p => p.key == clickedPiece.key));
        let index = shuffled.findIndex(p => p.key == clickedPiece.key);
        shuffled.splice(index, 1);
        setShuffled(shuffled);

        // Updating the board
        clickedPiece.location = "board";
        board[id - 1] = clickedPiece;
        setBoard(board);
        console.log(board);

        // reseting clicked piece
        setClickedPiece({key: -1});
      } else {
        console.log("POOP");
      }
    } else {
      if (hasPiece === "Yes") {
        event.currentTarget.setAttribute("has-piece", "No");

        board[id - 1].location = "shuffled";
        shuffled.push(board[id -1]);
        setShuffled(shuffled);

        board[id - 1] = 0;
        setBoard(board);

        setClickedPiece({key: -1});
      }
    }
  }

  useEffect(() => {
    console.log("called useeffect");
  });

  const fillRow = (spot, i, j) => {
    if (spot === 0) {
      return (
        <td
          key={(i+j)}
          id={(i+j)}
          has-piece="No"
          onClick={(e) => {clickBoard(e)}}
        >
          {i+j}
        </td>
      )
    } else {
      return (
        <td
            key={(i+j)}
            id={(i+j)}
            has-piece="No"
            onClick={(e) => {clickBoard(e)}}
        >
          <Piece 
            pieceDict={spot}
            clickPiece={clickPiece} 
            clickBoard={clickBoard}
            clicked={false}
            doubleClickPiece={doubleClickPiece}
          />
        </td>
      )
    }      
  }

  return (
    <>
      <div>
        <div className='pieces'>
          {shuffled.map((piece) => {
              return (
                <Piece 
                  pieceDict={piece} 
                  clickPiece={clickPiece} 
                  clickBoard={clickBoard}
                  clicked={(piece.key === clickedPiece.key)}
                  doubleClickPiece={doubleClickPiece}
                />
              )
            }
          )}
        </div>
        <table>
          <tbody>
            <tr key="1">
              {board.slice(0, 3).map((spot, i) => {
                return fillRow(spot, i, 1);
              })}
            </tr>
            <tr key="2">
              {board.slice(3, 6).map((spot, i) => {
                return fillRow(spot, i, 4);
              })}
            </tr>
            <tr key="3">
              {board.slice(6, 9).map((spot, i) => {
                return fillRow(spot, i, 7);
              })}
            </tr>
          </tbody>

      </table>
      <button onClick={() => {console.log(shuffled);}}>Pieces</button>
      <button onClick={() => {console.log(board, "\n", document.querySelector("tbody"));}}>Board</button>
      <button onClick={() => {console.log(clickedPiece)}}>ClickedPiece</button>

      </div>
    </>
  );
}

const Piece = (props) => {
  const {pieceDict, clickPiece, clickBoardPiece, clicked, doubleClickPiece} = props;
  if (clicked) {
    return (
      <article 
        className="Clicked" 
        key={pieceDict.key}
        id={pieceDict.key}
        onClick={(e) => {clickPiece(pieceDict, e)}} 
        onDoubleClick={(e)=>{doubleClickPiece(e)}}
      >
        <img src={pieceDict.type} alt="" style={{transform: "rotate(180deg)"}}/>
      </article>
      )
  } else {
    return (
      <article 
        key={pieceDict.key}
        id={pieceDict.key}
        onClick={(e) => {clickPiece(pieceDict, e)}}
        onDoubleClick={(e)=>{doubleClickPiece(e)}}
      >
        <img src={pieceDict.type} alt="" style={{transform: "rotate(90deg)"}}/>
      </article>
    )
  }
}

export default App;
